mapper.update:
UPDATE article
SET hitCount = hitCount + 1
WHERE id = #{id}

mapper.select:
SELECT hitCount
FROM article
WHERE id = #{id}

mapper.update:
UPDATE article
SET goodReactionPoint = goodReactionPoint + 1
WHERE id = #{id}

UPDATE article
SET badReactionPoint = badReactionPoint + 1
WHERE id = #{id}

UPDATE article
SET goodReactionPoint = goodReactionPoint - 1
WHERE id = #{id}

UPDATE article
SET badReactionPoint = badReactionPoint - 1
WHERE id = #{id}
